hooks.command:
python3 -c "
import sys, json, os
try:
    inp = json.load(sys.stdin)
except Exception:
    sys.exit(0)
path = inp.get('tool_input', {}).get('file_path', '')
if not path:
    sys.exit(0)
basename = os.path.basename(path)
if path.endswith('.swift'):
    print(f'[cross-platform] Edited {basename}')
    print('[cross-platform] Reminder: check if a web counterpart exists in multi-repo-nextjs/ for this feature.')
elif path.endswith(('.tsx', '.ts')) and 'multi-repo-nextjs' in path:
    print(f'[cross-platform] Edited {basename}')
    print('[cross-platform] Reminder: check if an iOS counterpart exists in multi-repo-ios/ for this feature.')
elif 'globals.css' in path or ('Color' in basename and path.endswith('.swift')):
    print('[design-token-sync] Design token file changed.')
    print('[design-token-sync] Run /design-token-sync to keep CSS variables and Swift Color extensions in sync.')
"
